import {
  ArrowCircleRightOutlined,
  ArrowCircleLeftOutlined,
} from "@mui/icons-material";
import styled from "styled-components";
import DripCollection from "../assets/existcollection.png";

const Container = styled.div`
  width: 100%;
  height: 100vh;
  display: flex;
  position: relative;
  overflow: hidden;
`;

const Pointer = styled.div`
  width: 50px;
  height: 50px;
  background-color: #ffffff;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  position: absolute;
  top: 0;
  bottom: 0;
  left: ${(props) => props.direction === "left" && "10px"};
  right: ${(props) => props.direction === "right" && "10px"};
  margin: auto;
  cursor: pointer;
  opacity: 0.5;
  z-index: 2;
`;

const Wrapper = styled.div`
  height: 100%;
  display: flex;
  transform: translateX(0vh);
`;

const Slidewide = styled.div`
  width: 100vw;
  height: 100vh;
  display: flex;
  align-items: center;
  background-color: #${(props) => props.bg};
`;

const ImageContainer = styled.div`
  flex: 1;
  height: 100%;
`;

const Image = styled.img`
  height: 80%;
  width: 45vh;
`;

const DataContainer = styled.div`
  flex: 1;
  padding: 50px;
`;

const Title = styled.h1`
  font-size: 70px;
`;

const Details = styled.p`
  margin: 50px 0px;
  font-size: 20px;
  font-weight: 500;
  letter-spacing: 3px;
`;

const Button = styled.button`
  padding: 10px;
  font-size: 20px;
  background-color: transparent;
  cursor: pointer;
`;

const Slide = () => {
  const handleClick = (direction) => {};

  return (
    <Container>
      <Pointer direction="left" onClick={() => handleClick("left")}>
        <ArrowCircleLeftOutlined />
      </Pointer>
      <Wrapper>
        <Slidewide>
          <ImageContainer>
            <Image src={DripCollection} />
          </ImageContainer>
          <DataContainer>
            <Title>COLLECTIONS</Title>
            <Details>
              THERE'S A REASON FOR EACH SEASON! LIMITED ITEMS PER NEW ARRIVAL.
            </Details>
            <Button>SHOP NOW</Button>
          </DataContainer>
        </Slidewide>
        <Slidewide bg="f5fafd">
          <ImageContainer>
            <Image src={DripCollection} />
          </ImageContainer>
          <DataContainer>
            <Title>COLLECTIONS</Title>
            <Details>
              THERE'S A REASON FOR EACH SEASON! LIMITED ITEMS PER NEW ARRIVAL.
            </Details>
            <Button>FALL SEASON</Button>
          </DataContainer>
        </Slidewide>
        <Slidewide bg="D2691E">
          <ImageContainer>
            <Image src={DripCollection} />
          </ImageContainer>
          <DataContainer>
            <Title>FAVORED ITEMS</Title>
            <Details>
              THERE'S A REASON FOR EACH SEASON! LIMITED ITEMS PER NEW ARRIVAL.
            </Details>
            <Button>SHOP NOW</Button>
          </DataContainer>
        </Slidewide>
      </Wrapper>
      <Pointer direction="right" onClick={() => handleClick("left")}>
        <ArrowCircleRightOutlined />
      </Pointer>
    </Container>
  );
};

export default Slide;
