import {
  ArrowCircleRightOutlined,
  ArrowCircleLeftOutlined,
} from "@mui/icons-material";
import styled from "styled-components";
import DripCollection from "../assets/existcollection.png";

const Container = styled.div`
  width: 100%;
  height: 100vh;
  display: flex;
`;

const Pointer = styled.div`
  width: 50px;
  height: 50px;
  background-color: #ffffff;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  position: absolute;
  top: 0;
  bottom: 0;
  margin: auto;
  left: ${(props) => props.direction === "left" && "10px"};
  right: ${(props) => props.direction === "right" && "10px"};
`;

const Wrapper = styled.div``;

const Slidewide = styled.div`
  display: flex;
  align-items: center;
  width: 100vw;
  height: 100vh;
`;

const ImgContainer = styled.div`
  flex: 1;
  height: 100%;
`;

const Img = styled.img`
  height: 80%;
`;

const DataContainer = styled.div`
  flex: 1;
  padding: 50px;
`;

const Title = styled.h1`
  font-size: 70px;
`;
const Details = styled.p``;
const Button = styled.button``;

const Slide = () => {
  return (
    <Container>
      <Pointer direction="left">
        <ArrowCircleLeftOutlined />
      </Pointer>
      <Wrapper>
        <Slidewide>
          <ImgContainer>
            <Img src={DripCollection} />
          </ImgContainer>
          <DataContainer>
            <Title>COLLECTIONS SET</Title>
            <Details>
              THERE'S A REASON FOR EACH SEASON! LIMITED ITEMS PER NEW ARRIVALS.
            </Details>
            <Button>SHOP NOW</Button>
          </DataContainer>
        </Slidewide>
      </Wrapper>
      <Pointer direction="right">
        <ArrowCircleRightOutlined />
      </Pointer>
    </Container>
  );
};

export default Slide;
