import {
  Instagram,
  Pinterest,
  Facebook,
  Twitter,
  MailOutline,
  Room,
  Phone,
} from "@mui/icons-material";
import styled from "styled-components";

const Container = styled.div`
  display: flex;
`;

const Left = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  padding: 20px;
`;

const Logo = styled.h1``;

const Details = styled.p`
  margin: 20px 0px;
`;

const SocialContainer = styled.div`
  display: flex;
`;

const SocialIcon = styled.div`
  width: 40px;
  height: 40px;
  border-radius: 50%;
  color: #ffffff;
  background-color: #${(props) => props.color};
  align-items: center;
  justify-content: center;
  margin-right: 10px;
`;

const Center = styled.div`
  flex: 1;
`;

const Title = styled.h3`
  margin-bottom: 30px;
`;

const Links = styled.ul`
  margin: 0;
  padding: 0;
  list-style: none;
  display: flex;
  flex-wrap: wrap;
`;

const NavLinks = styled.li`
  width: 50%;
  margin-bottom: 10px;
`;

const Right = styled.div`
  flex: 1;
  padding: 20px;
`;

const ContactList = styled.div`
  margin-bottom: 20px;
  display: flex;
  align-items: center;
`;

const Payment = styled.div`
  width: 50%;
`;

const Footer = () => {
  return (
    <Container>
      <Left>
        <Logo>Noirmay</Logo>
        <Details>This is a placeholder text.</Details>
        <SocialContainer>
          <SocialIcon color="3B5999">
            <Instagram />
          </SocialIcon>
          <SocialIcon color="E4405F">
            <Facebook />
          </SocialIcon>
          <SocialIcon color="55ACEE">
            <Twitter />
          </SocialIcon>
          <SocialIcon color="E60023">
            <Pinterest />
          </SocialIcon>
        </SocialContainer>
      </Left>
      <Center>
        <Title>Useful Links</Title>
        <Links>
          <NavLinks>Home</NavLinks>
          <NavLinks>My Account</NavLinks>
          <NavLinks>Latest Release</NavLinks>
          <NavLinks>Infants</NavLinks>
          <NavLinks>Big Kids</NavLinks>
          <NavLinks>Accessories</NavLinks>
          <NavLinks>WishList</NavLinks>
          <NavLinks>Cart</NavLinks>
          <NavLinks>Terms</NavLinks>
        </Links>
      </Center>
      <Right>
        <Title>Contact</Title>
        <ContactList>
          <Room style={{ marginRight: "10px" }} />
          P. Sherman, 42 Wallaby Way, Sydney
        </ContactList>
        <ContactList>
          <Phone style={{ marginRight: "10px" }} />
          Give us a call <br>+1 234 567 8910</br>
        </ContactList>
        <ContactList>
          <MailOutline style={{ marginRight: "10px" }} />
          support@noirmay.com
        </ContactList>
        <Payment />
        <p>&copy; 2022 Noirmay</p>
      </Right>
    </Container>
  );
};

export default Footer;
