import {
  Instagram,
  Pinterest,
  Facebook,
  Twitter,
  MailOutline,
  Room,
  Phone,
} from "@mui/icons-material";
import styled from "styled-components";

const Container = styled.div`
  display: flex;
`;

const Left = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  padding: 20px;
`;

const Logo = styled.h1``;

// const Details = styled.p`
//   margin: 20px 0px;
// `;

const SocialContainer = styled.div`
  display: flex;
`;

const SocialIcon = styled.div`
  width: 20px;
  height: 40px;
  border-radius: 50%;
  color: #000000;
  background-color: #${(props) => props.color};
  align-items: center;
  justify-content: center;
  margin-right: 10px;
`;

const Center = styled.div`
  flex: 1;
`;

const Title = styled.h3`
  margin-bottom: 30px;
`;

const Links = styled.ul`
  margin: 0;
  padding: 0;
  list-style: none;
  display: flex;
  flex-wrap: wrap;
`;

const NavLinks = styled.li`
  width: 50%;
  margin-bottom: 60px;
`;

const Right = styled.div`
  flex: 1;
  padding: 20px;
`;

const ContactList = styled.div`
  margin-bottom: 20px;
  display: flex;
  align-items: center;
`;

const Payment = styled.img`
  width: 50%;
  margin-top: 20px;
  margin-bottom: 20px;
`;

const Footer = () => {
  return (
    <Container>
      <Left>
        <Logo>Noirmay</Logo>
        {/* <Details>This is a placeholder text.</Details> */}

        <Payment src="https://i.ibb.co/Qfvn4z6/payment.png" />
        <p>&copy; 2022 Noirmay</p>
      </Left>
      <Center>
        <Title>Useful Links</Title>
        <Links>
          <NavLinks>Home</NavLinks>
          <NavLinks>My Account</NavLinks>
          <NavLinks>Latest Release</NavLinks>
          <NavLinks>Infants</NavLinks>
          <NavLinks>Big Kids</NavLinks>
          <NavLinks>Accessories</NavLinks>
          <NavLinks>WishList</NavLinks>
          <NavLinks>Cart</NavLinks>
          <NavLinks>Terms</NavLinks>
        </Links>
      </Center>
      <Right>
        <Title>Contact</Title>
        <ContactList>
          <Room style={{ marginRight: "10px" }} />
          P. Sherman, 42 Wallaby Way, Sydney
        </ContactList>
        <ContactList>
          <Phone style={{ marginRight: "10px" }} />
          Give us a call +1 234 567 8910
        </ContactList>
        <ContactList>
          <MailOutline style={{ marginRight: "10px" }} />
          support@noirmay.com
        </ContactList>
        <SocialContainer>
          <SocialIcon>
            <Instagram />
          </SocialIcon>
          <SocialIcon>
            <Facebook />
          </SocialIcon>
          <SocialIcon>
            <Twitter />
          </SocialIcon>
          <SocialIcon>
            <Pinterest />
          </SocialIcon>
        </SocialContainer>
      </Right>
    </Container>
  );
};

export default Footer;
